uniform sampler2D ii;uniform float le;uniform vec3 es;uniform vec4 ps;uniform vec4 mi;uniform vec4 ms;uniform vec3 ss;uniform bool drawPath;varying vec4 hg;varying vec3 hc;varying vec3 gg;void main(){float a=le*0.01;gl_FragColor.rgb=hg.rgb;gl_FragColor.a=1.0;if(!drawPath){
#ifdef GLOBAL
gl_FragColor=(texture2D(ii,gl_PointCoord)*gl_FragColor);if(gl_FragColor.r>=0.15&&gl_FragColor.g>=0.15&&gl_FragColor.b>=0.15){gl_FragColor.rgb-=vec3(0.15,0.15,0.15);}
#else
gl_FragColor=(texture2D(ii,gl_PointCoord)*gl_FragColor)+vec4(0.27,0.27,0.27,0.0);
#endif
gl_FragColor.a*=a;}else{gl_FragColor.a*=(0.3*a);}if(gl_FragColor.r<=1e-6&&gl_FragColor.g<=1e-6&&gl_FragColor.b<=1e-6){gl_FragColor.rgb=vec3(0.1,0.1,0.1);gl_FragColor.a=0.1;}}

attribute vec3 $auxpos1;uniform mat4 pm;uniform mat4 ls;uniform sampler2D op;uniform vec2 ol;uniform sampler2D em;uniform vec2 oe;uniform float ei;uniform float mp;uniform float sm;uniform vec2 ip;uniform bool drawPath;uniform sampler2D pl;uniform bool ee;varying vec4 hg;varying vec3 hc;varying vec3 gg; $linearInterpolator  $lonlat2position  $localTrans  $curveInter void main(void){float a=fract($auxpos1.x*2.0/oe.x);float b=floor($auxpos1.x*2.0/oe.y)/oe.y;vec4 c=texture2D(em,vec2(a,b));if(abs(c.r-$auxpos1.x)<Epsilon){vec3 d=c.gba;a=fract(($auxpos1.x*2.0+1.0)/oe.x);b=floor(($auxpos1.x*2.0+1.0)/oe.y)/oe.y;vec4 e=texture2D(em,vec2(a,b));vec3 f=e.gba;vec3 g,h,i,j;float k,l;float m=1.0;float n=0.0;
#ifdef GLOBAL
d=lonlat2position(d);f=lonlat2position(f);g=(d+f)*0.5;h=normalize(g);i=d-f;k=length(i);g=h*(length(g)+k*0.6);if(k<=1000.0){l=32.0;}else if(k<=10000.0){l=24.0;}else if(k<=500000.0){l=18.0;}else if(k<=1000000.0){l=40.0;}else{l=floor(k*0.00001);}if(drawPath){n=$auxpos1.z;}else{n=mod($auxpos1.z+(l*2.0+1.0)/ei*sm,l*2.0+1.0);}if((n>=l)&&(n<(l*2.0+1.0))){m=-1.0;n-=l;}vec3 o=normalize(i);vec3 p=g+o*(k*0.6*m);float q=n/l;q=clamp(q,0.0,1.0);if(m<=0.0){j=getPoint(q,g,p,f,f);}else{j=getPoint(q,d,d,p,g);}hc=normalize(j);
#else
d=wgs84ToWebMerc(d);f=wgs84ToWebMerc(f);g=(d+f)*0.5;h=vec3(0.0,0.0,1.0);i=d-f;k=length(i);g=g+h*(k*0.3);if(k<=1000.0){l=48.0;}else if(k<=10000.0){l=42.0;}else if(k<=100000.0){l=32.0;}else if(k<=1000000.0){l=24.0;}else if(k<=2000000.0){l=36.0;}else{l=floor(k*0.000006);}if(drawPath){n=$auxpos1.z;}else{n=mod($auxpos1.z+(l*2.0+1.0)/ei*sm,l*2.0+1.0);}if((n>=l)&&(n<(l*2.0+1.0))){m=-1.0;n-=l;}vec3 o=normalize(i);vec3 p=g+o*(k*0.3*m);float q=n/l;q=clamp(q,0.0,1.0);if(m<=0.0){j=getPoint(q,g,p,f,f);}else{j=getPoint(q,d,d,p,g);}hc=vec3(0.0,0.0,1.0);
#endif
gl_Position=pm*ls*vec4(j,1.0);gg=j;a=fract($auxpos1.y/ol.x);b=floor($auxpos1.y/ol.y)/ol.y;float r=(texture2D(op,vec2(a,b))).r;float s=getLinearValue(ip,r);if(!drawPath){gl_PointSize=mp*pow(2.718,s);}hg=texture2D(pl,vec2(s,0.5));}else{vec3 j=vec3(0.0,0.0,0.0);gl_Position=pm*ls*vec4(j,1.0);gg=j;if(!drawPath) gl_PointSize=mp;hg=vec4(1.0,1.0,0.0,1.0);hc=normalize(j);}}
