#ifdef GLOBAL
mat4 r=getTransMat($position);q=toRotationMat(r);o=(r*vec4(n,1.0)).xyz;vec4 s=vec4(0.0,0.0,1.0,0.0);p=(q*s).xyz;
#else
o=wgs84ToWebMerc($position);o+=n;p=vec3(1.0,0.0,1.0);
#endif
gl_Position=me*sp*vec4(o,1.0);dc=normalize(p);hc=o;cd=texture2D(ei,vec2(k,0.5));}else{float t=getScope(es,k);mat4 r=getTransMat($position);float u=($auxpos1.y-lp/2.0)*(20.0/lp);float v=($auxpos1.z-lp/2.0)*(20.0/lp);float w=exp(-0.0831*(u*u+v*v));float x=atan(u,v);u=w*cos(2.0*x);v=w*sin(2.0*x);float y=sqrt(u*u+v*v)*getScope(es,k);vec3 o=(r*vec4(t*($auxpos1.y/lp-0.5),t*($auxpos1.z/lp-0.5),y,1.0)).xyz;vec3 p=normalize(o);gl_Position=me*sp*vec4(o,1.0);dc=p;hc=o;gh=$auxpos1.yz/(lp-1.0);}}
