#ifdef GL_ES
precision mediump float;
#endif
uniform vec3 io;uniform vec4 es;uniform vec4 ps;uniform vec4 oi;uniform vec3 ee;uniform float pl;uniform vec3 ss;varying vec2 dg;varying vec3 dd;varying vec3 cc;varying vec4 gg;const vec3 a=vec3(1.0,1.0,1.0);const vec3 b=vec3(1.0,1.0,1.0);const vec3 c=vec3(0.4,0.4,0.4);void main(){vec3 d=normalize(cc-ee);vec3 e=normalize(dd);vec3 f=normalize(reflect(d,e));float g=max(dot(f,io),.001);vec3 h=c*oi.rgb*oi.w*pow(g,16.0);vec3 i=a*ps.rgb*ps.w*clamp(dot(e,io),.0,1.0);vec3 j=b*es.rgb*es.w;vec3 k=vec3(j+i+h);float l=abs(dg.x-0.5)/0.5;gl_FragColor=(1.0-l)*vec4(ss,1.0)+l*gg;gl_FragColor*=vec4(k,1.0);float m=pl*0.01;gl_FragColor.w=m;}

attribute vec3 $position;attribute vec3 $auxpos1;uniform mat4 ls;uniform mat4 po;uniform bool pm;uniform float sp;uniform sampler2D el;uniform sampler2D is;uniform vec2 op;uniform float mm;uniform vec2 ep;uniform float ms;uniform vec2 ol;uniform bool os;uniform sampler2D lp;varying vec3 dd;varying vec3 cc;varying vec2 dg;varying vec4 gg; $lonlat2position  $translationMat  $linearInterpolator  $localTrans  $expoEaseOut const float a=0.0;const float b=1.0;const float c=2.0;const float d=3.0;void main(void){float e=fract($auxpos1.x/op.x);float f=floor($auxpos1.x/op.y)/op.y;float g=(texture2D(el,vec2(e,f))).r;float h=(texture2D(is,vec2(e,f))).r;float i=mod(ms,mm);float j=getExpoEaseOutValue(i,g,h,mm);if(os) j=h;float k=getLinearValue(ol,j);if(pm){float l=200000.0;if($auxpos1.y==a||$auxpos1.y==d){l=getScope(ep,k);}else if($auxpos1.y==b||$auxpos1.y==c){l=1.25*getScope(ep,k);}if($auxpos1.y==a) dg=vec2(0.0,0.0);else if($auxpos1.y==d) dg=vec2(0.0,1.0);else if($auxpos1.y==b) dg=vec2(1.0,0.0);else if($auxpos1.y==c) dg=vec2(1.0,1.0);float m=$auxpos1.z*2.0*PI;vec3 n=vec3(l*cos(m),l*sin(m),0.0);vec3 o,p;mat4 q=mat4(1.0);
#ifdef GLOBAL
mat4 r=getTransMat($position);q=toRotationMat(r);o=(r*vec4(n,1.0)).xyz;vec4 s=vec4(0.0,0.0,1.0,0.0);p=(q*s).xyz;
#else
o=wgs84ToWebMerc($position);o+=n;p=vec3(1.0,0.0,1.0);
#endif
gl_Position=ls*po*vec4(o,1.0);dd=normalize(p);cc=o;gg=texture2D(lp,vec2(k,0.5));}else{float t=getScope(ep,k);mat4 r=getTransMat($position);float u=($auxpos1.y-sp/2.0)*(20.0/sp);float v=($auxpos1.z-sp/2.0)*(20.0/sp);float w=exp(-0.0831*(u*u+v*v));float x=atan(u,v);u=w*cos(2.0*x);v=w*sin(2.0*x);float y=sqrt(u*u+v*v)*getScope(ep,k);vec3 o=(r*vec4(t*($auxpos1.y/sp-0.5),t*($auxpos1.z/sp-0.5),y,1.0)).xyz;vec3 p=normalize(o);gl_Position=ls*po*vec4(o,1.0);dd=p;cc=o;dg=$auxpos1.yz/(sp-1.0);}}
