#ifdef GL_ES
precision mediump float;
#endif
uniform vec3 ps;uniform vec4 lm;uniform vec4 ol;uniform vec4 sm;uniform vec3 se;uniform float po;uniform vec3 pi;varying vec2 dc;varying vec3 hc;varying vec3 cg;varying vec4 gg;const vec3 a=vec3(1.0,1.0,1.0);const vec3 b=vec3(1.0,1.0,1.0);const vec3 c=vec3(0.4,0.4,0.4);void main(){vec3 d=normalize(cg-se);vec3 e=normalize(hc);vec3 f=normalize(reflect(d,e));float g=max(dot(f,ps),.001);vec3 h=c*sm.rgb*sm.w*pow(g,16.0);vec3 i=a*ol.rgb*ol.w*clamp(dot(e,ps),.0,1.0);vec3 j=b*lm.rgb*lm.w;vec3 k=vec3(j+i+h);float l=abs(dc.x-0.5)/0.5;gl_FragColor=(1.0-l)*vec4(pi,1.0)+l*gg;gl_FragColor*=vec4(k,1.0);float m=po*0.01;gl_FragColor.w=m;}

attribute vec3 $position;attribute vec3 $auxpos1;uniform mat4 ss;uniform mat4 ll;uniform bool io;uniform float pl;uniform sampler2D mp;uniform sampler2D em;uniform vec2 le;uniform float es;uniform vec2 pm;uniform float ls;uniform vec2 mo;uniform bool ip;uniform sampler2D li;varying vec3 hc;varying vec3 cg;varying vec2 dc;varying vec4 gg; $lonlat2position  $translationMat  $linearInterpolator  $localTrans  $expoEaseOut const float a=0.0;const float b=1.0;const float c=2.0;const float d=3.0;void main(void){float e=fract($auxpos1.x/le.x);float f=floor($auxpos1.x/le.y)/le.y;float g=(texture2D(mp,vec2(e,f))).r;float h=(texture2D(em,vec2(e,f))).r;float i=mod(ls,es);float j=getExpoEaseOutValue(i,g,h,es);if(ip) j=h;float k=getLinearValue(mo,j);if(io){float l=200000.0;if($auxpos1.y==a||$auxpos1.y==d){l=getScope(pm,k);}else if($auxpos1.y==b||$auxpos1.y==c){l=1.25*getScope(pm,k);}if($auxpos1.y==a) dc=vec2(0.0,0.0);else if($auxpos1.y==d) dc=vec2(0.0,1.0);else if($auxpos1.y==b) dc=vec2(1.0,0.0);else if($auxpos1.y==c) dc=vec2(1.0,1.0);float m=$auxpos1.z*2.0*PI;vec3 n=vec3(l*cos(m),l*sin(m),0.0);vec3 o,p;mat4 q=mat4(1.0);
#ifdef GLOBAL
mat4 r=getTransMat($position);q=toRotationMat(r);o=(r*vec4(n,1.0)).xyz;vec4 s=vec4(0.0,0.0,1.0,0.0);p=(q*s).xyz;
#else
o=wgs84ToWebMerc($position);o+=n;p=vec3(1.0,0.0,1.0);
#endif
gl_Position=ss*ll*vec4(o,1.0);hc=normalize(p);cg=o;gg=texture2D(li,vec2(k,0.5));}else{float t=getScope(pm,k);mat4 r=getTransMat($position);float u=($auxpos1.y-pl/2.0)*(20.0/pl);float v=($auxpos1.z-pl/2.0)*(20.0/pl);float w=exp(-0.0831*(u*u+v*v));float x=atan(u,v);u=w*cos(2.0*x);v=w*sin(2.0*x);float y=sqrt(u*u+v*v)*getScope(pm,k);vec3 o=(r*vec4(t*($auxpos1.y/pl-0.5),t*($auxpos1.z/pl-0.5),y,1.0)).xyz;vec3 p=normalize(o);gl_Position=ss*ll*vec4(o,1.0);hc=p;cg=o;dc=$auxpos1.yz/(pl-1.0);}}
